mapper.select:
SELECT
    *
FROM TB_SITEINFO
WHERE
    CO_NAME = #{co_name}
AND CO_DOMAIN = #{co_domain}

mapper.update:
UPDATE TB_SITEINFO
    SET CO_PW = #{co_pw}
WHERE 
	CO_NAME = #{co_name}
AND CO_DOMAIN = #{co_domain}

mapper.delete:
DELETE FROM TB_SITEINFO
WHERE 
	CO_NAME = #{co_name} 
AND CO_DOMAIN = #{co_domain}
